     */
    public List<String> selectWords(String s, int n) {
        List<String> res = new ArrayList<String>();
        if (s.length() == 0) return res;

        char[] ss = s.toCharArray();
        int nc = 0;
        for (int i = 0; i < ss.length; i++) {
            if (ss[i] =='') continue;
            nc++;
        }

        if (nc == n) res.add(s);

        for (int i = 0; i < ss.length; i++) {
            if (ss[i] =='') continue;
            if (nc < n) {
                List<String> tmp = selectWords(s.substring(i), n - nc);
                res.addAll(tmp);
            }

            for (int j = i + 1; j < ss.length; j++) {
                if (ss[j] =='') continue;

                List<String> tmp = selectWords(s.substring(i, j + 1), n - nc);
                res.addAll(tmp);
            }
        }
        return res;
    }
