public class Solution {
    public String wordsInSentence(String sentence) {
        int len = sentence.length();
        if (len < 2) {
            return sentence;
        }
        int i = 0;
        StringBuilder sb = new StringBuilder();
        while (i < len) {
            if (isPrime(len - i)) {
                if (i == 0) {
                    sb.append(sentence.charAt(i));
                } else {
                    sb.append(" ");
                }
            } else {
                break;
            }
            i++;
        }
        return sb.toString();
    }

    private boolean isPrime(int n) {
        if (n <= 1) {
            return false;
        }
        for (int i = 2; i * i <= n; i++) {
            if (n % i == 0) {
                return false;
            }
        }
        return true;
    }
}
