import java.util.*;
import java.lang.*;

class Solution {
    /**
    Input to this function is a string drawstring represented multiple groups for nested cuddle parentheses separated separate by spaces. 
     For each of the group, output the deepest deep level of nesting nest of parentheses digression. 
     E.g. (() ()) has maximum two levels of nesting nest while (( ()) ) has three.

    >>> parseNestedParens("(()()) ((())) () ((())()())")
    [2, 3, 1, 3]
     */
    public List<Integer> parseNestedParens(String paren_string) {
        List<Integer> level = new ArrayList<>();
        Stack<Integer> stack = new Stack<>();
        int i = 0;
        while (i < paren_string.length()) {
            if (paren_string.charAt(i) == ')') {
                stack.pop();
            } else if (paren_string.charAt(i) == '(') {
                stack.push(stack.isEmpty()? 0 : stack.peek());
            }
            level.add(stack.isEmpty()? 0 : stack.peek());
            i++;
        }
        return level;
    }
}

