        if (n < 2) return new ArrayList<Double>();

        Collections.sort(numbers);

        int left = 0;
        int right = n - 1;

        double min_diff = Double.MAX_VALUE;

        List<Double> result = new ArrayList<Double>();

        while (left < right) {

            double mid = (left + right) / 2.0;

            if (mid == 0) return new ArrayList<Double>();

            double diff = Math.abs(numbers.get(left) - numbers.get(mid));

            if (diff < min_diff) {
                min_diff = diff;
                result.clear();
                result.add(numbers.get(left));
                result.add(numbers.get(mid));
            } else if (diff == min_diff) {
                result.add(numbers.get(left));
                result.add(numbers.get(mid));
            }

            if (mid < n / 2.0) left = mid + 1;
            else right = mid - 1;
        }

        return result;
    }
