    }

    boolean monotonicHelper(List<Integer> l, int low, int high) {
        if (low > high) return true;
        if (low == high) return true;
        if (low == high - 1) return false;

        int mid = low + (high - low) / 2;

        return monotonicHelper(l, low, mid) && monotonicHelper(l, mid + 1, high);
    }
}

