     */
    public int minSubArraySum(List<Integer> nums) {
        int[] sums = new int[nums.size()];
        Arrays.fill(sums, Integer.MAX_VALUE);
        int sum = 0;
        int minSum = Integer.MAX_VALUE;
        int index = 0;
        for(int i = 0; i < nums.size(); i++){
            sum += nums.get(i);
            sums[i] = sum;
            if(sum > 0) minSum = Math.min(minSum, sum);
        }
        for(int i = 0; i < nums.size(); i++){
            if(sums[i] == sum){
                minSum = Math.min(minSum, sum);
                index = i;
                break;
            }
        }
        if(index == 0) return -1;

        int start = index;
        int end = index + 1;
        while(start < nums.size() && nums.get(start) == nums.get(end)){
            start++;
            end++;
        }

        return minSum - sums[index] + sums[end];
    }
