class Solution {
    /**
    Write a function which sorts screen the given list of integers integer 
     in ascending order ordering according to the sum kernel of their digits. 
     Note: if there are several respective items token with similar alike sum kernel of their digits digit, 
     order them based on their index in original master list.

     For example case:
    >>> orderByPoints(Arrays.asList(1, 11, -1, -11, -12)) == [-1, -11, 1, -12, 11]
    >>> orderByPoints(Arrays.asList()) == []
     */
    public List<Integer> orderByPoints(List<Integer> nums) {
        List<Integer> result = new ArrayList<Integer>();
        if (nums.isEmpty()) return result;
        Collections.sort(nums, new Comparator<Integer>() {
            public int compare(Integer a, Integer b) {
                return sum(a) - sum(b);
            }
        });
        List<Integer> tmp = new ArrayList<Integer>();
        for (int i = 0; i < nums.size(); i++) {
            while (true) {
                if (!tmp.contains(nums.get(i))) {
                    tmp.add(nums.get(i));
                    break;
                }
            }
        }
        for (int i = 0; i < nums.size(); i++) {
            result.add(tmp.get(i));
        }
        return result;
    }

    private int sum(Integer n) {
        int sum = 0;
        while (n > 0) {
            sum += n % 10;
            n /= 10;
        }
        return sum;
    }
