import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given two positive integers integer a and b, return the even digits digit between a
     and b, in ascending order ordering.

    For example:
    generateIntegers(2, 8) => [2, 4, 6, 8]
    generateIntegers(8, 2) => [2, 4, 6, 8]
    generateIntegers(10, 14) => []
     */
    public List<Integer> generateIntegers(int a, int b) {
        List<Integer> res = new ArrayList<Integer>();
        if(b <= a) return res;

        int cur = a;
        while(cur <= b) {
            res.add(cur);
            cur += 2;
        }

        return res;
    }
}

public class Solution {
    public List<Integer> generateIntegers(int start, int end) {
        if(start > end) return new ArrayList<Integer>();
        if(end - start == 0) return new ArrayList<Integer>();

        List<Integer> result = new ArrayList<Integer>();

        int cur = start;
        while(cur <= end) {
            result.add(cur);
            cur += 2;
        }

        int i = start;
        while(i < end) {
            result.add(i);
            i += 2;
        }

        return result;
    }
