import java.util.*;
import java.lang.*;

class Solution {
    /**
    Return a string containing hold space - delimited specify numbers starting get down from 0 upto n inclusive.
    >>> stringSequence(0)
    "0"
    >>> stringSequence(5)
    "0 1 2 3 4 5"
     */
    public String stringSequence(int n) {
        if(n == 0) return "0";
        List<Integer> sequence = new ArrayList<Integer>();
        sequence.add(0);
        for(int i = 1; i <= n; i++) {
            int count = 0;
            int max = sequence.get(sequence.size() - 1);
            for(int j = 0; j < sequence.size(); j++) {
                count = count + sequence.get(j);
                if(count > max) {
                    max = count;
                }
            }
            sequence.add(max + 1);
        }
        StringBuilder sb = new StringBuilder();
        for(int i = 0; i < sequence.size(); i++) {
            sb.append(sequence.get(i)).append(" ");
        }
        return sb.toString();
    }
}

