     */
    public List<Double> findClosestElements(List<Double> numbers) {
        int n = numbers.size();

        if (n < 2) return new ArrayList<Double>();

        Collections.sort(numbers);

        double x = numbers.get(0);
        double y = numbers.get(1);

        double minDist = Math.abs(x - y);

        for (int i = 2; i < n; ++i) {
            double d = Math.abs(numbers.get(i) - x);
            if (d < minDist) {
                minDist = d;
                x = numbers.get(i);
            }
        }

        for (int i = n - 1; i >= 2; --i) {
            double d = Math.abs(numbers.get(i) - y);
            if (d < minDist) {
                minDist = d;
                y = numbers.get(i);
            }
        }

        List<Double> result = new ArrayList<Double>(2);
        result.add(x);
        result.add(y);

        return result;
    }
