    false
    >>> isPrime(1)
    false
     */
    public boolean isPrime(int n) {
        if (n == 1) return false;
        if (n < 3) return true;
        if (n % 2 == 0) return false;
        boolean[] sieve = new boolean[n];
        Arrays.fill(sieve, true);
        for (int i = 3; i * i <= n; i += 2) {
            if (!sieve[i]) continue;
            int j = i * i;
            int tmp = n / i;
            for (; j <= tmp; j += i) {
                sieve[j] = false;
            }
        }
        return sieve[n];
    }
