class Solution {
    /**
    Given a string of words discussion, return a list of words split on whitespace, if no whitespaces exists exist in the text you 
     should split on commas comma',' if no commas exists exist you should return the number of lower low - case letters with odd order club in the 
     alphabet, ord('a') = 0, ord('b') = 1,... ord('z') = 25
    Examples
    splitWords("Hello world!") == ["Hello", "world!"]
    splitWords("Hello,world!") == ["Hello", "world!"]
    splitWords("abcdef") == 3
     */
    public Object splitWords(String txt) {
        if(txt == null || txt.length() == 0)
            return new ArrayList<String>();
        String[] split = txt.split("\\s+");
        List<String> ret = new ArrayList<String>();
        for(String s: split)
            if(s!= null && s.length() > 0)
                ret.add(s);
        return ret;
    }
