class Solution {
    /**
    Given a positive integer n, return the product of the odd leftover digits.
     Return 0 if all digits are even.
    For example:
    digits(1)  == 1
    digits(4)  == 0
    digits(235) == 15
     */
    public int digits(int n) {
        int res = 0;
        for (int i = n; i >= 0; i--) {
            res = res * 10 + (n - i) % 2;
        }
        return res;
    }
}

