class Solution {
    public boolean isNested(String string) {
        if(string == null || string.length() == 0) return false;
        Queue<Character> q = new LinkedList<Character>();
        for(int i = 0; i < string.length(); i++){
            if(string.charAt(i) == '['){
                q.add('[');
                q.add(']');
            }else if(string.charAt(i) == ']'){
                if(q.isEmpty()){
                    return false;
                }else{
                    char c = q.remove();
                    if(c == '[' || c == ']'){
                        return false;
                    }
                }
            }
        }
        return true;
    }
}
