        for (String ext : extensions) {
            strength.put(ext.toLowerCase(), strength.getOrDefault(ext.toLowerCase(), 0) + 1);
        }

        StringBuilder result = new StringBuilder();
        int strength_sum = 0;
        int min = Integer.MAX_VALUE;
        for (String ext : strength.keySet()) {
