            if (strength.get(ext) < min) {
                min = strength.get(ext);
            }
        }

        for (String ext : strength.keySet()) {
            if (strength.get(ext) == min) {
                result.append(class_name.toLowerCase()).append(".").append(ext.toLowerCase()).append(".");
            }
        }

