            result.add(sum);
            return;
        }
        TrieNode cur = root.children[word.charAt(idx) - 'a'];
        if (cur == null) {
            return;
        }
        dfs(cur, idx + 1, word, sum);
        dfs(root, idx + 1, word, sum);
