import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given a positive floating point number, it can be disintegrate into 
     and integer region (largest integer smaller than given number) and decimals 
     (leftover function always little than 1). 

     Return the decimal part of the number.
    >>> truncateNumber(3.5)
    0.5
     */
    public double truncateNumber(double number) {
        double part = number;
        long part_l = (long) number;
        if(number == part_l) {
            return 0;
        }

        long integer_part = (long) part;
        double decimals = part - integer_part;
        if(decimals < 0) {
            return 0;
        }

        int integer_decimals = (int) (number - integer_part);
        double decimal_part = number - integer_part - integer_decimals;
        return decimal_part;
    }
