import java.util.*;
import java.lang.*;

class Solution {
    /**
    Input to this function is a string represented multiple groups for nested parenthesis branch by space. 
     For each of the group, output the deep level of nest of parentheses. 
     E.g. (() ()) has maximal two levels of nest while (( ()) ) has three.

    >>> parseNestedParens("(()()) ((())) () ((())()())")
    [2, 3, 1, 3]
     */
    public List<Integer> parseNestedParens(String paren_string) {
        List<Integer> ret = new ArrayList<Integer>();
        int level = 0;
        Stack<Character> stack = new Stack<Character>();
        String s = paren_string.trim();
        for (int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);
            if (c == '(') {
                stack.push(c);
                level++;
            } else if (c == ')') {
                if (stack.empty()) {
                    ret.add(level);
                    level--;
                } else {
                    stack.pop();
                    level--;
                }
            }
        }
        return ret;
    }
}

