        dp[0][0] = 1;
        for (int i = 1; i < n; ++i) {
            dp[i][i] = 1;
            for (int j = 0; j < i; ++j) {
                if (string.charAt(j) == string.charAt(i)) {
                    dp[i][j] = dp[j][i-1] + dp[i-1][j];
                } else {
                    dp[i][j] = dp[j][i-1];
                }
            }
        }

        String prefix = "";
        for (int i = n-1; i >= 0; --i) {
            if (dp[i][i] > 0) {
                res.add(prefix);
                prefix += string.charAt(i);
            }
        }
        return res;
