        while (n > 1) {
            int r = temp % 10;
            int c = n / 10;
            temp = n;
            if (r == c) {
                result = r;
                n = r;
            } else if (r < c) {
                result = r;
                n = r;
            } else if (r > c) {
                n = c;
            }
        }
        return result;
    }
