public class Solution {
    public int largestDivisor(int n) {
        int[] d = new int[n + 1];
        Arrays.fill(d, 1);
        for (int i = 2; i <= n; i++) {
            for (int j = 1; j < i; j++) {
                if (n % i == 0) {
                    d[i] = Math.min(d[i], d[j]);
                }
            }
        }
        int largest = 0;
        for (int i = 2; i <= n; i++) {
            if (d[i] > largest) {
                largest = d[i];
            }
        }
        return largest;
    }
