    >>> primeFib(5)
    89
     */
    public int primeFib(int n) {
        if(n < 2) return 2;
        if(n == 2) return 3;
        if(n == 3) return 5;
        int a = 2, b = 3;
        while(n > 1) {
            int c = a + b;
            a = b;
            b = c;
            if(c % 2 == 0) n--;
        }
        return a;
    }
