        int start = 0;
        int end = lst.size() - 1;
        while (index <= end) {
            int mid = index + (end - index) / 2;
            if (lst.get(mid) < lst.get(index)) {
                swap(lst, index, mid);
                index++;
            } else if (lst.get(mid) > lst.get(index)) {
