class Solution {
    /**
    Given an array of integers, sort the integers that equal between 1 and 9 inclusive, 
     change by reversal the resulting array, and then replace each digit by its corresponding name from 
     " One ", " Two ", " Three ", " Four ", " Five ", " Six ", " Seven ", " Eight ", " Nine ".

    For example:
      arr = [2, 1, 1, 4, 5, 8, 2, 3]
            -> sort arr -> [1, 1, 2, 2, 3, 4, 5, 8]
            -> reverse arr -> [8, 5, 4, 3, 2, 2, 1, 1]
      return ["Eight", "Five", "Four", "Three", "Two", "Two", "One", "One"]

      If the array is empty, return an empty array:
      arr = []
      return []

      If the array has any strange number ignore it:
      arr = [1, -1, 55]
            -> sort arr -> [-1, 1, 55]
            -> reverse arr -> [55, 1, -1]
      return = ["One"]
     */
    public List<String> byLength(List<Integer> arr) {
        List<String> ret = new ArrayList<String>();
        if (arr == null || arr.size() == 0) {
            return ret;
        }

        Map<Integer, String> map = new HashMap<Integer, String>();
        map.put(1, "One");
        map.put(2, "Two");
        map.put(3, "Three");
        map.put(4, "Four");
        map.put(5, "Five");
        map.put(6, "Six");
        map.put(7, "Seven");
        map.put(8, "Eight");
        map.put(9, "Nine");

        List<Integer> temp = new ArrayList<Integer>();
        for (int i : arr) {
            int t = i;
            if (t < 1) {
                continue;
            }
            temp.add(t);
        }
        arr = temp;

        while (!arr.isEmpty()) {
            int length = arr.size();
            if (length < 1) {
                break;
            }
            int start = 0;
            int end = length - 1;
            while (start + 1 < end) {
                int mid = start + (end - start) / 2;
                if (arr.get(mid) < 1) {
                    start = mid + 1;
                } else if (arr.get(mid) > 9) {
                    end = mid - 1;
                } else {
                    break;
                }
            }
            if (start + 1 == end) {
                if (arr.get(start) < 1) {
                    continue;
                }
                String str = map.get(arr.get(start));
                ret.add(str);
                arr.remove(start);
            } else {
                String str = map.get(arr.get(start));
                ret.add(str);
                String str1 = map.get(arr.get(end));
                ret.add(str1);
                arr.remove(end);
                arr.remove(start);
            }
        }
