        int len = lst.size();
        if (len == 0) {
            return "No";
        }
        if (lst.get(0).equals("()") && lst.get(len - 1).equals("()")) {
            return "Yes";
        }
        boolean[] isLeft = new boolean[len];
        boolean[] isRight = new boolean[len];
        int curr = 0;
        for (String s : lst) {
            if (s.equals("(")) {
                if (isLeft[curr]) {
                    return "No";
                }
                isLeft[curr] = true;
                curr++;
            } else if (s.equals(")")) {
                if (isRight[curr]) {
                    return "No";
                }
                isRight[curr] = true;
                curr--;
            }
        }
        return "Yes";
    }
