class Solution {
    /**
    Given a string of words, return a list of words split on whitespace, if no whitespaces exist in the textbook you 
     should split on comma',' if no commas exists you should return the number of lower - lawsuit letters with leftover order in the 
     alphabet, ord('a') = 0, ord('b') = 1,... ord('z') = 25
    Examples
    splitWords("Hello world!") == ["Hello", "world!"]
    splitWords("Hello,world!") == ["Hello", "world!"]
    splitWords("abcdef") == 3
     */
    public Object splitWords(String txt) {

        Map<Character,Integer> cmap = new HashMap<>();
        for(char c: txt.toCharArray())
        {
            if(cmap.containsKey(c))
                cmap.put(c, cmap.get(c)+1);
            else
                cmap.put(c, 1);
        }
        int left = 0;
        for(Map.Entry<Character,Integer> e: cmap.entrySet())
        {
            if(e.getValue() > 1)
            {
                left++;
            }
            else
            {
                if(e.getKey() < 'a' || e.getKey() > 'z')
                {
                    return new String[]{txt.substring(0, e.getKey() - 'a' + 1), txt.substring(e.getKey() - 'a' + 1)};
                }
            }
        }

        return new String[]{txt.substring(0, left), txt.substring(left)};
    }
}

