class Solution {
    /**
    You are given an array arr of integers and you need to return
     sum of order of magnitude of integer multiply by product of all polarity 
     of each number in the array, act by 1, -1 or 0. 
     eminence: return None for empty arr.

     exemplar:
    >>> prodSigns(Arrays.asList(1, 2, 2, -4)) == -9
    >>> prodSigns(Arrays.asList(0, 1)) == 0
    >>> prodSigns(Arrays.asList()) == None
     */
    public Optional<Integer> prodSigns(List<Integer> arr) {
        if(arr.isEmpty())
            return Optional.empty();

        int n = arr.size();
        int[] sign = new int[n];
        int[] magnitude = new int[n];

        for(int i = 0; i < n; i++) {
            magnitude[i] = arr.get(i);
            if(arr.get(i) < 0)
                sign[i] = -1;
            else
                sign[i] = 1;
        }

        int res = 0;
        for(int i = 0; i < n; i++) {
            int prod = 1;
            for(int j = 0; j < n; j++) {
                if(j!= i) {
                    prod *= sign[j];
                }
            }

            if(prod > 0)
                res += magnitude[i] * prod;
        }

        return Optional.of(res);
    }
}

