     */
    public List<Integer> evenOddCount(int num) {
        List<Integer> res = new ArrayList<Integer>();
        int odd = 0, even = 0;
        while (num!= 0) {
            int digit = num % 10;
            if (digit % 2 == 0) {
                even++;
            } else {
                odd++;
            }
            num = num / 10;
        }
