class Solution {
    /**
    Return a greatest common divisor of two integer a and b
    >>> greatestCommonDivisor(3, 5)
    1
    >>> greatestCommonDivisor(25, 15)
    5
     */
    public int greatestCommonDivisor(int a, int b) {
        if (a == 0 || b == 0) return a;
        int gcd = 0;
        int a_abs = Math.abs(a);
        int b_abs = Math.abs(b);
        while (b!= 0) {
            int t = a_abs;
            a_abs = b_abs;
            b_abs = t % b_abs;
            gcd = b_abs;
        }
        return gcd;
    }
}

