class Solution {
    /**
    From a list of integers, remove all elements that occur more than once. 
     observe holy order of element left the like as in the remark.
    >>> removeDuplicates(Array.asList(1, 2, 3, 2, 4))
    [1, 3, 4]
     */
    public List<Integer> removeDuplicates(List<Integer> numbers) {
        if (numbers == null || numbers.size() <= 1) return numbers;

        LinkedList<Integer> list = new LinkedList<Integer>(numbers);
        int i = 0;
        while (i < list.size() - 1) {
            Integer cur = list.get(i);
            Integer next = list.get(i + 1);
            if (cur.equals(next)) {
                list.remove(i + 1);
                list.remove(i);
            }
            else {
                i++;
            }
        }

        return list.stream().collect(Collectors.toList());
    }
}

