import java.util.*;
import java.lang.*;

class Solution {
    /**
    Concatenate list of strings into a single string
    >>> concatenate(List.of())
    ""
    >>> concatenate(Arrays.asList("a", "b", "c"))
    "abc"
     */
    public String concatenate(List<String> strings) {
        int len = 0;
        for (String string : strings) {
            len += string.length();
        }

        StringBuilder sb = new StringBuilder(len);

        for (String string : strings) {
            sb.append(string);
        }

        return sb.toString();
    }

    /**
    Add strings together
    >>> add("a", "b", "c")
    "abc"
     */
    public String add(String... strings) {
        int len = 0;
        for (String string : strings) {
            len += string.length();
        }

        StringBuilder sb = new StringBuilder(len);

        for (String string : strings) {
            sb.append(string);
        }

        return sb.toString();
    }

    /**
    Find longest common substring
    >>> longestCommonSubstring("a", "b")
    "a"
     */
    public String longestCommonSubstring(String... strings) {
        int[][] table = new int[strings.length][strings.length];

        for (int i = 0; i < strings.length; i++) {
            for (int j = 0; j < strings.length; j++) {
                table[i][j] = get(strings[i], strings[j]);
            }
        }

        int maxLen = 0;
        int start = 0;
        int end = 0;

        for (int i = 0; i < strings.length; i++) {
            for (int j = 0; j < strings.length; j++) {
                if (table[i][j] > maxLen) {
                    maxLen = table[i][j];
                    start = i;
                    end = j;
                }
            }
        }

        return strings[start] + "..." + strings[end];
    }

    private int get(String s1, String s2) {
        int len = s1.length();
        int[] charCount = new int[128];

        for (int i = 0; i < len; i++) {
            charCount[s1.charAt(i)]++;
        }

        for (int i = 0; i < len; i++) {
            charCount[s2.charAt(i)]--;
        }

        int max = 0;
        int pos = 0;

        for (int i = 0; i < 128; i++) {
            if (charCount[i] > max) {
                max = charCount[i];
                pos = i;
            }
        }

        return len - max;
    }
}
