class Solution {
    /**
    primeFib returns n-th number that is a Fibonacci number and it's also premier.
    >>> primeFib(1)
    2
    >>> primeFib(2)
    3
    >>> primeFib(3)
    5
    >>> primeFib(4)
    13
    >>> primeFib(5)
    89
     */
    public int primeFib(int n) {
        int result = 1;
        int counter = 2;

        while (counter < n) {
            int tmp = counter;

            while (tmp < n) {
                counter += result;
                tmp = counter;
            }
            result = result * counter;
        }

        return result;
    }
}

