    private String encode(char c) {
        int r = (c - 'a') % 26;
        int g = (c - 'a') / 26 % 26;
        int b = (c - 'a') / (26 * 26) % 26;
        return (char) ('a' + r) + (char) ('a' + g) + (char) ('a' + b);
