import java.util.*;
import java.lang.*;

class Solution {
    /**
    brackets is a string of " < " and " > ". 
     return True if every opening bracket has a comparable closing bracket.

    >>> correctBracketing("<")
    false
    >>> correctBracketing("<>")
    true
    >>> correctBracketing("<<><>>")
    true
    >>> correctBracketing("><<>")
    false
     */
    public boolean correctBracketing(String brackets) {
        if (brackets.length() % 2 == 1) return false;
        int len = brackets.length();
        int[] stack = new int[len];
        int stackPtr = 0;
        for (int i = 0; i < len; i++) {
            char c = brackets.charAt(i);
            if (c == '<') {
                stack[stackPtr++] = i;
            } else if (c == '>') {
                if (stackPtr == 0) return false;
                int j = stack[--stackPtr];
                if (brackets.charAt(j)!= c) return false;
            }
        }
        return stackPtr == 0;
    }
