        for (int i = len - 1; i >= 0; i--)
            res.add(String.valueOf(arr2[i]));
        // replace each digit by its corresponding name
        for (int i = 0; i < len; i++)
            res.set(i, res.get(i) + " " + names[arr2[i]]);
