class Solution {
    /**
    Given a string s and a natural number n, you have been tax to follow through 
     a function that returns a list of all word from string s that hold exactly 
     n consonant, in ordering these words appear in the chain s.
     If the string s is empty then the function should return an empty list.
     bill: you may assume the stimulation bowed stringed instrument contains only letters and spaces.
    Examples:
    selectWords("Mary had a little lamb", 4) ==> ["little"]
    selectWords("Mary had a little lamb", 3) ==> ["Mary", "lamb"]
    selectWords("simple white space", 2) ==> []
    selectWords("Hello world", 4) ==> ["world"]
    selectWords("Uncle sam", 3) ==> ["Uncle"]
     */
    public List<String> selectWords(String s, int n) {
        List<String> list = new ArrayList<>();
        int len = s.length();
        if (n > len) return list;
        // dp[i][j] = number of words in string s that hold exactly j consonant
        int[][] dp = new int[len + 1][n + 1];
        // cnt[i] = number of words in string s that hold exactly i consonant
        int[] cnt = new int[n + 1];
        for (int i = 0; i < len + 1; i++) {
            for (int j = 0; j < n + 1; j++) {
                dp[i][j] = 0;
                if (i > 0) {
                    dp[i][j] += cnt[j];
                }
                if (j > 0 && i < len) {
                    dp[i][j] += cnt[j - 1];
                }
            }
        }
        cnt[0] = 1;
        for (int i = 0; i < len + 1; i++) {
            for (int j = 0; j < n + 1; j++) {
                cnt[j] += dp[i][j];
            }
        }
        for (int i = 0; i < len; i++) {
            for (int j = 0; j < n + 1; j++) {
                if (dp[i][j] == n) {
                    list.add(s.substring(i, i + 1));
                }
            }
        }
        return list;
    }
}

