     */
    public List<Integer> maximum(List<Integer> arr, int k) {
        if (arr.size() < k) {
            return null;
        }
        List<Integer> result = new ArrayList<Integer>();
        int length = arr.size();
        for (int i = 0; i < length; i++) {
            int cur = arr.get(i);
            int index = binarySearch(result, cur);
            if (index!= -1) {
                result.set(index, Math.max(result.get(index), cur));
            } else {
                result.add(cur);
            }
        }
        return result;
    }

    private int binarySearch(List<Integer> list, int x) {
        int start = 0;
        int end = list.size() - 1;
        while (start <= end) {
            int mid = (start + end) / 2;
            if (list.get(mid) < x) {
                start = mid + 1;
            } else if (list.get(mid) > x) {
                end = mid - 1;
            } else {
                return mid;
            }
        }
        return -1;
    }
