    tri(3) = [1, 3, 2, 8]
     */
    public List<Integer> tri(int n) {
        if (n <= 0) return new ArrayList<Integer>();
        List<Integer> list = new ArrayList<Integer>();
        for (int i = 1; i <= n; i++) {
            int temp = i;
            while (temp > 1) {
                list.add(temp);
                temp = temp - 1;
                list.add(temp + 1);
            }
        }
        return list;
    }
