            } else {
                master.put(i, 1);
            }
        }

        PriorityQueue<Integer> q = new PriorityQueue<>((a, b) -> master.get(a) - master.get(b));
        master.keySet().stream().forEach(n -> q.offer(n));
        while (!q.isEmpty()) {
            result.add(q.poll());
        }
        return result;
    }
