        int result = 0;
        int i = 0;
        int j = 0;
        if(operand.size() < 2) {
            return result;
        }
        while(i < operator.size() && j < operand.size()) {
            if(operator.get(i) == "+") {
                result += operand.get(j);
                j++;
            } else if(operator.get(i) == "-") {
                result -= operand.get(j);
                j++;
            } else if(operator.get(i) == "*") {
