     */
    public double meanAbsoluteDeviation(List<Double> numbers) {
        Collections.sort(numbers);
        int n = numbers.size();
        double median = (n % 2 == 0)? (numbers.get(n / 2) + numbers.get(n / 2 - 1)) / 2 : numbers.get(n / 2);
        double sum = 0.0;
        for (Double num : numbers) sum += Math.abs(num - median);
        return sum / n;
    }
