class Solution {
    /**
    From a list of integers, remove alleleme nts that occur more than once.
   Keep order of elements left the sameas in  the in p ut.
    >>> removeDuplicates(Array.asList(1, 2, 3, 2, 4))
    [1, 3, 4]
     */
    public List<Integer> removeDuplicates(List<Integer> numbers) {
        int n = numbers.size();
        if (n <= 2) {
            return numbers;
        }
        List<Integer> res = new ArrayList<>(n);
        int last = -1;
        for (int i = 0; i < n; i++) {
            int current = numbers.get(i);
            if (last == current) {
                continue;
            }
            last = current;
            res.add(current);
        }
        return res;
    }
}
