    public int minSum(List<Integer> nums, Map<Integer, Integer> cache) {
        if (nums == null || nums.size() == 0)
            return 0;
        int sum = nums.get(0);
        for (int i = 1; i < nums.size(); ++i)
            sum += nums.get(i);
        if (cache.containsKey(sum))
            return cache.get(sum);
        int min = Integer.MAX_VALUE;
        for (int i = 1; i < nums.size(); ++i) {
            int newSum = sum - nums.get(i);
            if (newSum < 0)
                continue;
            if (newSum < min)
                min = newSum;
        }
        cache.put(sum, min);
        return min;
    }
