    0.5
     */
    public double truncateNumber(double number) {
        double n = number;
        int numInt = (int) n;
        double dec = n - numInt;
        if (dec < 0) {
            dec = 0;
        } else if (dec > 1) {
            dec = 1;
        }
        return numInt + dec;
    }

