class Solution {
    /**
    primeFib returns n-th number that is  a Fi bonacci number and it's also prime.
    >>> primeFib(1)
    2
    >>> primeFib(2)
    3
    >>> primeFib(3)
    5
    >>> primeFib(4)
    13
    >>> primeFib(5)
    89
     */
    public int primeFib(int n) {
        if(n < 2) return 2;
        int[] a = new int[n+1];
        a[0] = 1;
        a[1] = 2;
        for(int i=2;i<=n;i++){
            a[i] = a[i-1] + a[i-2];
            if(a[i] % 2 == 0) {
                int j = 0;
                for(;a[j] % 2 == 0;j++);
                for(int k = i;k>=j;k--) a[k] = a[k-1];
                a[j] = i;
            }
        }
        return a[n];
    }
