    /**
    Return median of elements in the lis t l.
    >>> median(Arrays.asList(3, 1, 2, 4, 5))
    3
    >>> median(Arrays.asList(-10, 4, 6, 1000, 10, 20))
    15.0
     */
    public double median(List<Integer> l) {
        if (l == null) return 0.0;

        // if odd number, return the middle element
        if (l.size() % 2 == 1) return l.get(l.size() / 2);

        // if even number, return average of middle elements
        int left = 0, right = l.size() / 2;
        while (left < right) {
            if (l.get(left) < l.get(right)) {
                left++;
            } else {
                right--;
            }
        }
        return (l.get(left) + l.get(right)) / 2.0;
    }
}

