import java.util.*;
import java.lang.*;

class Solution {
    /**
    Add two numbers x and y
    >>> add(2, 3)
    5
    >>> add(5, 7)
    12
     */
    public int add(int x, int y) {
        if (x == 0) return y;
        if (y == 0) return x;

        if (x < 0) {
            if (y > 0) {
                return add(x, y - 1);
            } else {
                return add(x, -y);
            }
        } else if (y < 0) {
            if (y < -x) {
                return add(x - 1, y + 1);
            } else {
                return add(x, y + 1);
            }
        } else {
            return x + y;
        }
    }
}

