        while (m!= 0) {
            res[--i] = (char) ((m & 1) + '0');
            m >>= 1;
        }
        res[--i] = '0';
        for (; i >= 0; i--) {
            if (res[i]!= '0') {
                res[--j] = '1';
            }
        }
        return new String(res) + "db";
    }
}

