    public List<Integer> sortArray(List<Integer> array) {
        int len = array.size();
        if (len <= 1) {
            return array;
        }
        int[] tmp = new int[len];
        int[] sum = new int[len];
        for (int i = 0; i < len; i++) {
            sum[i] = sum[i + 1] = array.get(i);
            tmp[i] = i;
